import React, { useState, useEffect } from 'react';

interface LogoProps {
  size?: 'sm' | 'md' | 'lg';
  className?: string;
}

const Logo: React.FC<LogoProps> = ({ size = 'md', className = '' }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [retryCount, setRetryCount] = useState(0);

  const sizeClasses = {
    sm: 'h-6 w-6',
    md: 'h-8 w-8',
    lg: 'h-12 w-12'
  };

  const fallbackSvg = {
    sm: 'data:image/svg+xml;base64,',
    md: 'data:image/svg+xml;base64,',
    lg: 'data:image/svg+xml;base64,'
  };

  const handleImageLoad = () => {
    setImageLoaded(true);
    setImageError(false);
  };

  const handleImageError = () => {
    console.warn('Failed to load logo image, using fallback');
    setImageError(true);
    setImageLoaded(false);
  };

  const handleRetry = () => {
    if (retryCount < 2) {
      setRetryCount(prev => prev + 1);
      setImageError(false);
      setImageLoaded(false);
    }
  };

  useEffect(() => {
    // Reset state when retry count changes
    setImageError(false);
    setImageLoaded(false);

    // Add timeout to prevent infinite loading
    const timeout = setTimeout(() => {
      if (!imageLoaded && !imageError) {
        console.warn('Logo loading timeout, using fallback');
        setImageError(true);
      }
    }, 5000); // 5 second timeout

    return () => clearTimeout(timeout);
  }, [retryCount, imageLoaded, imageError]);

  // If we've tried too many times or had an error, use fallback
  if (imageError || retryCount >= 2) {
    return (
      <img
        src={fallbackSvg[size]}
        alt="Olynk.ai Logo"
        className={`${sizeClasses[size]} object-contain ${className}`}
      />
    );
  }

  // Show loading state
  if (!imageLoaded) {
    return (
      <div 
        className={`${sizeClasses[size]} bg-gray-200 dark:bg-gray-700 rounded animate-pulse ${className}`}
        style={{ 
          minWidth: size === 'sm' ? '24px' : size === 'md' ? '32px' : '48px',
          minHeight: size === 'sm' ? '24px' : size === 'md' ? '32px' : '48px'
        }}
      />
    );
  }

  return (
    <img
      src={size === 'sm' ? "/assets/Olynk.AI_Logo-small.png" : "/assets/Olynk.AI_Logo.png"}
      alt="Olynk.ai Logo"
      className={`${sizeClasses[size]} object-contain ${className} ${
        !imageLoaded ? 'opacity-0' : 'opacity-100'
      } transition-opacity duration-300`}
      onLoad={handleImageLoad}
      onError={handleImageError}
      style={{ 
        minWidth: size === 'sm' ? '24px' : size === 'md' ? '32px' : '48px',
        minHeight: size === 'sm' ? '24px' : size === 'md' ? '32px' : '48px'
      }}
    />
  );
};

export default Logo; 